          ...t,
          image: this.getImageForTopic(t.name) // Assign local image based on topic name
        }));
      },
      error: err => console.error('Error fetching topics:', err)
    });
  }
  // ✅ Helper: map topic name to local image
  getImageForTopic(topicName) {
    switch (topicName.toLowerCase()) {
      case 'html':
        return 'html.png';
      case 'css':
        return 'css.png';
      case 'javascript':
        return 'js.png';
      case 'angular':
        return 'angular.png';
      default:
        return 'default.png';
    }
  }
